import axios from "axios";
import { createAsyncThunk } from "@reduxjs/toolkit";


axios.defaults.baseURL = 'https://62d82adc9c8b5185c7846b84.mockapi.io'

export async function fetchC(){
  const {data} = axios.get(`/contacts`)
  return data
}




   export const fetchContact = createAsyncThunk(
      "https://62d82adc9c8b5185c7846b84.mockapi.io/contacts", async()=>{
        const contacts = await fetchC()
        console.log(contacts);
        return contacts
      }
    );

