import React from "react";
import ContactCard from "components/ContactCard/ContactCard";
import { useSelector } from "react-redux";
import { contactValue, filterValue } from "redux/slice";
import { useGetContactsQuery } from "redux/slice";

function ContactList() {
  const { data, error, isLoading } = useGetContactsQuery();

  const filterValue = useSelector((state) => state.filter.filter);

   const  filterContact = data.filter(({ name }) =>
      name.toLowerCase().startsWith(filterValue.toLowerCase())
    );


  return (
    <ul>
      {filterContact && filterContact.map((contact, index) => (
        <ContactCard contact={contact} key={index} />
      ))}
    </ul>
  );
}

export default ContactList;
